import React, { Component } from 'react';

import iconBarra from '../../images/iconbarra.png'
import fotoPerfil from '../../images/fotoPerfil.jpg'
import user from '../../images/user.png'
import iconNome from '../../images/icon_nome.png'
import usPic from '../../images/us-pic.png'
import profileGuilherme from '../../images/userProfileGuilherme.jpeg'

import Post from '../../components/post/posts.js';


import './css/style.css'
import './css/animate.css'
import './css/bootstrap.min.css'
import './css/line-awesome.css'
import './css/line-awesome-font-awesome.min.css'
import './css/font-awesome.min.css'
import './css/jquery.mCustomScrollbar.min.css'
import './css/responsive.css'
import './vendor/fontawesome-free/css/all.min.css'
import './lib/slick/slick.css'
import './lib/slick/slick-theme.css'

import firebase from 'firebase';

class Home extends Component {
    
    constructor(props){
        super(props);
        this.state = {
            posts: [
            ]
        }

        this.deslogar = this.deslogar.bind(this);
    }

    componentDidMount(){

        firebase.auth().onAuthStateChanged((user)=>{
            if(!user){
                window.location = '/login';
            }
        })

        let newPosts = this.state.posts;

        let banco = [
            {nome: "Guilherme",
            user: "gui_webeer",
            conteudo: "Aqui ta o conteudo do post 1",
            curtidas: 10,
            comentarios: 10},
            {nome: "William",
            user: "ferris",
            conteudo: "Aqui ta o conteudo do post 2",
            curtidas: 20,
            comentarios: 20},
            {nome: "Mariana",
            user: "maribove",
            conteudo: "Aqui ta o conteudo do post 3",
            curtidas: 30,
            comentarios: 30}
        ]

        for (let i = 0; i < 3; i++) {
            newPosts.push(<Post nome={banco[i].nome} username={banco[i].user} conteudo={banco[i].conteudo} curtidas={banco[i].curtidas} comentarios={banco[i].comentarios}/>)
          }

        this.setState({
            posts: newPosts
        })
    }

    deslogar(){
        firebase.auth().signOut();
        console.log("Deslogou")
    }

    render(){
        return(
            <div className="wrapper">   
                <header>
                    {/* <!-- inicio logotipo-->*/}
                    <div class="container">
                        <div class="header-data">
                            <div class="logo">
                                <a href="paginainicial-feed.html" title=""> <img src={iconBarra}/></a>
                            </div> {/* <!--logotipo acaba--> */}
                            
                            {/* <!-- inicio barra de pesquisa--> */}
                            <div class="search-bar">
                                <form>
                                    <input type="text" name="search" placeholder="Pesquisar Conteúdo..."/>
                                    <button type="submit"><i class="fas fa-search"></i></button>
                                </form>
                            </div> {/* <!--barra de pesquisa acaba --> */}
                            
                            {/* <!-- inicio botao menu--> */}
                            <div class="menu-btn">
                                <a href="#" title=""><i class="fa fa-bars"></i></a>
                            </div>{/* <!--botao menu acaba--> */}

                            {/* <!--usuário header--> */}
                            <div class="user-account">
                                <div class="user-info">
                                    <img src={user}/>
                                    <a href="#" title="">@usuário</a>
                                    
                                </div>
                                
                            </div> {/* <!--usuário header acaba--> */}
                        </div>{/* <!--header-data acaba--> */}
                    </div>
                </header>{/* <!--header acaba--> */}
        
                <main>
                    <div class="main-section">
                        <div class="container">
                            <div class="main-section-data">
                                <div class="row">

                                    { /* <!-- menu esquerda --> */}
                                    <div class="col-lg-3 col-md-4 pd-left-none no-pd">
                                        <div class="main-left-sidebar no-margin">
                                            <div class="user-data full-width">
                                                <div class="user-profile">
                                                    <div class="username-dt">
                                                        <div class="usr-pic">
                                                            <img src={profileGuilherme}/>
                                                        </div>
                                                    </div>
                                                    <div class="user-specs">
                                                        <h3> Nome </h3>
                                                        <span> @usuário</span>
                                                    </div>
                                                </div>
                                                <ul class="user-fw-status">
                                                    
                                                    <li>
                                                        <a href="#" class="com"> <i class="fas fa-user-alt"></i> Meu Perfil </a>
                                                    </li>

                                            
                                                    <li>
                                                        <a href="feedlivros.html" title=""> Feed Livros</a>
                                                    </li>
                                                    <li>
                                                        <a href="feedseries.html" title=""> Feed Séries</a>
                                                    </li>
                                                    
                                                    <li>
                                                        <a href="feedfilmes.html" title=""> Feed Filmes</a>
                                                    </li>

                                                    <li>
                                                        <a href="#" class="com"> <i class="fas fa-cog"></i> Configurações </a>
                                                    </li>
                                                    
                                                    <li>
                                                        <a href="#" class="com" onClick={this.deslogar}> <i class="fas fa-sign-out-alt"></i> Deslogar </a>
                                                    </li>
                                                </ul>
                                            </div> {/* <!--menu lateral acaba-->*/ }
                                            
                                            { /*<!--infos rede-->*/ }
                                            <div class="tags-sec full-width">
                                                <ul>
                                                    <li><a href="#" title="">Sobre</a></li>
                                                    <li><a href="#" title="">Central de Ajuda</a></li>
                                                    <li><a href="#" title="">Termos de Serviço</a></li>
                                                    <li><a href="#" title="">Política de Privacidade</a></li>
