import React, { Component } from 'react';
import './index.css'

class Login extends Component {
    
    constructor(props){
        super(props);
        this.state = {
            form: {
                username: "1",
                password: "2"
            }
        }
        this.dadosForm = this.dadosForm.bind(this);
    }

    dadosForm(e) {
        let form = this.state.form;
        form[e.target.name] = e.target.value;
        this.setState({form: form})
    }

    render(){
        return(
            <div class="page">
                <div className="container">
                    <h1 className="titulo"> Infinity </h1>

                    <div className="campo">
                        <label className="labelLogin"> Usuário </label> <br/>
                        <input type="text" placeholder="Usuário" id="userLogin" name="userLogin"
                               className="formLogin" value={this.state.form.username} onChange={this.dadosForm} />
                    </div>

                    <div className="campo">
                        <label className="labelLogin"> Senha </label> <br/>
                        <input type="text" placeholder="Senha" id="userPassword" name="userPassword" 
                               className="formLogin" value={this.state.form.password} onChange={this.dadosForm} /> <br/>
                        <a href="#" id="forgotPassword"> Esqueceu a senha? </a>
                    </div>

                    <button className="btLogin">Entrar</button> <br/>
                    <button className="btLogin">Cadastrar</button>

                </div>
            </div>
        )
    }

}

export default Login;
