import React, { Component } from 'react';
import './index.css'

class Login extends Component {
    
    constructor(props){
        super(props);
        this.state = {
            username: "",
            password: "",
            login: false,
        }
        this.entrar = this.entrar.bind(this);
    }

    entrar(e) {
        const {username, password} = this.state
        alert(username, password);
    }

    render(){
        return(
            <div class="page">
                <div className="container">
                    <h1 className="titulo"> Infinity </h1>

                    <div className="campo">
                        <label className="labelLogin"> Usuário </label> <br/>
                        <input type="text" placeholder="Usuário" id="userLogin" name="userLogin"
                               className="formLogin" value={this.state.username} onChange={this.entrar} />
                    </div>

                    <div className="campo">
                        <label className="labelLogin"> Senha </label> <br/>
                        <input type="text" placeholder="Senha" id="userPassword" name="userPassword" 
                               className="formLogin" value={this.state.password} onChange={this.entrar} /> <br/>
                        <a href="#" id="forgotPassword"> Esqueceu a senha? </a>
                    </div>

                    <button className="btLogin" onClick={this.entrar}>Entrar</button> <br/>
                    <button className="btLogin">Cadastrar</button>

                </div>
            </div>
        )
    }

}

export default Login;
