import React, { Component } from 'react';
import './index.css'

class Login extends Component {
    
    constructor(props){
        super(props);
        this.state = {
            username: "guilherme",
            password: "123"            
        }

        this.trocaEmail = this.trocaEmail.bind(this);
        this.trocaSenha = this.trocaSenha.bind(this);
    }

    trocaEmail(e){
        let valorDigitado = e.target.value;
        this.setState({username: valorDigitado})    
    }

    trocaSenha(e) {
        let valorDigitadoSenha = e.target.value;
        this.setState({password: valorDigitadoSenha})
    }


    render(){
        return(
            <div className="page">
                <div classNameName="container">
                    <h1 classNameName="titulo"> Infinite </h1>

                    <div classNameName="campo">
                        <label classNameName="labelLogin"> Usuário </label> <br/>
                        <input type="text" placeholder="Usuário" id="userLogin" name="username"
                               classNameName="formLogin" value={this.state.username} onChange={this.trocaEmail} />
                    </div>

                    <div classNameName="campo">
                        <label classNameName="labelLogin"> Senha </label> <br/>
                        <input type="text" placeholder="Senha" id="password" name="userPassword" 
                               classNameName="formLogin" value={this.state.password} onChange={this.trocaSenha} /> <br/>
                        <a href="#" id="forgotPassword"> Esqueceu a senha? </a>
                    </div>

                    <button classNameName="btLogin" onClick={this.entrar}>Entrar</button> <br/>
                    <button classNameName="btLogin">Cadastrar</button>

                </div>
            </div>
        )
    }

}

export default Login;
