import React, { Component } from 'react';

import Post from '../../components/post/posts.js';
import MenuUsuario from '../../components/menuUsuario';
import Header from '../../components/header';
import SeguindoPerfil from '../../components/profile';
import Following from '../../components/following/index.js';

import '../../css/templateHome/animate.css';
import '../../css/templateHome/bootstrap.min.css';
import '../../css/templateHome/jquery.mCustomScrollbar.min.css'
import '../../css/templateHome/responsive.css';
import '../../css/templateHome/style.css';
import '../../css/templateHome/cssFonts/line-awesome.css'
import '../../css/templateHome/cssFonts/line-awesome-font-awesome.min.css'
import '../../css/templateHome/cssFonts/font-awesome.min.css'
import '../../css/templateHome/vendor/fontawesome-free/css/all.min.css';
import '../../css/templateHome/lib/slick/slick.css';
import '../../css/templateHome/lib/slick/slick-theme.css';

import firebase from 'firebase';

class Profile extends Component {
    
    constructor(props){
        super(props);
        this.state = {
            userLog: "",
            nameLog: "",
            posts: [
            ],
            profileContent: []
        }

        let element = document.getElementsByClassName("active");
        let title = element.getAttribute("title");
        console.log(title)
        
        this.loadPosts = this.loadPosts.bind(this);
        this.alteraState = this.alteraState.bind(this);
        this.authValidation = this.authValidation.bind(this);
    }

    authValidation() {
        // Verifica se está logado
        firebase.auth().onAuthStateChanged((user)=>{
            if(!user){
                window.location = '/login';
            } else {
                // Puxa informações do usuário logado
                firebase.firestore().collection('users')
                .doc(user.uid)    
                .get()
                .then((snapshot) => {
                    this.setState({ userLog: snapshot.data().username})
                    this.setState({ nameLog: snapshot.data().nome})
                    })
                .catch(() => {
                    console.log("Algo deu errado!")
                })
            }
        })
    }


    componentDidMount(){

        this.authValidation();

        this.loadPosts();

        let newPosts = this.state.posts;

        let banco = [
            {nome: "Guilherme",
            user: "gui_webeer",
            conteudo: "Aqui ta o conteudo do post 1",
            curtidas: 10,
            comentarios: 10},
            {nome: "William",
            user: "ferris",
            conteudo: "Aqui ta o conteudo do post 2",
            curtidas: 20,
            comentarios: 20},
            {nome: "Mariana",
            user: "maribove",
            conteudo: "Aqui ta o conteudo do post 3",
            curtidas: 30,
            comentarios: 30}
        ]

        for (let i = 0; i < 3; i++) {
            newPosts.push(<Post nome={banco[i].nome} username={banco[i].user} conteudo={banco[i].conteudo} curtidas={banco[i].curtidas} comentarios={banco[i].comentarios}/>)
          }

        this.setState({
            posts: newPosts
        }, () => {this.setState({
            profileContent: this.state.posts
        })})
    }

    alteraState() {
        let content = [];
        content.push()
        this.setState({profileContent: content})
        
    }
    


    loadPosts(){
        firebase.firestore().collection('posts')
        .get()
        .then((snapshot) => {
            let lista = [];

            snapshot.forEach((doc) => {
                lista.push({
                    id: doc.id,
                    curtidas: doc.data().curtidas,
                })
            })

            console.log(lista)
        })
    }

    render(){
        return(
            <div className="wrapper">   

                {/* Chama o componente <Header/> com props */}
                <Header username={this.state.userLog}/>
        
                <main>
                    <div class="main-section">
                        <div class="container">
                            <div class="main-section-data">
                                <div class="row">

                                    { /* Chama o componente <MenuUsuario/> com props */}
                                    <MenuUsuario name={this.state.nameLog} username={this.state.userLog} />

                                    <div class="col-lg-6 col-md-8 no-pd">
                                        <div class="main-ws-sec">

                                            {/* Chama o componente <ProfileHeader/> */}
                                            <SeguindoPerfil/>

                                            <div class="posts-section">

                                                {/* {this.state.profileContent} */}
                                                <Following/>

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div> 
                    </div>
                </main>
	        </div>
        )
    }
}

export default Profile;
